import {useEffect, useState} from 'react';

const get = url => {
  const [data, setData] = useState(null);

  useEffect(() => {
    updateInfo();
  }, []);

  const updateInfo = async () => {
    const data = await fetch(`//localhost:8080/api${url}`)
      .then(result => result.json());

    setData(data);
  };

  return data
};

export {get}
