import {useEffect, useState} from 'react';

export const get = (url) => {

  const [data, setData] = useState(null);

  useEffect(() => {
    const updateInfo = async () => {
      const data = await fetch(`//localhost:8080/api${url}`)
        .then(result => result.json());

      setData(data);
    };
    updateInfo();
  }, []);

  return data
};
