import React, {useEffect, useState} from 'react';
import {useFetch} from '../../utils/fetch-hook'
import {API_ENDPOINTS} from '../../utils/constants'
import './index.css';
import {Fa500Px, FaAccessibleIcon} from 'react-icons/fa';
const el = [Fa500Px, FaAccessibleIcon];


function Header(props) {

  icons
  const {loading, data} = useFetch(API_ENDPOINTS.personInfo);
  return (
    <header className="Header">
      {loading ? (
        <div>Loading...</div>
      ) : (
        <div>
          <h1 className="Header--title">
            {data.firstName} {data.secondName}
          </h1>
          <hr className="Header--separator" />
          <h2 className="Header--subtitle">{data.aboutMe}</h2>
        </div>
      )}
    </header>
  );
}

export default Header;
