import React, {useEffect, useState} from 'react';
import './index.css';

function Header(props) {

  const [name, setName] = useState('Tadas');
  const [surname, setSurname] = useState('Obolevicius');
  const [aboutMe, setAboutMe] = useState('Software engineer');

  useEffect(() => {
    const updateInfo = async () => {
      const personInfo = await fetch('//localhost:8080/api/person/info')
        .then(result => result.json());

      setName(personInfo.firstName);
      setSurname(personInfo.secondName);
      setAboutMe(personInfo.aboutMe);
    };
    updateInfo();
  }, []);

  return (
    <header className="Header">
      <h1 className="Header--title"> {name} {surname}</h1>
      <hr className="Header--separator"/>
      <h2 className="Header--subtitle">{aboutMe}</h2>
    </header>
  );
}

export default Header;
