import React from 'react';
import './index.css';
import {useFetch} from "../../utils/fetch-hook";
import {API_ENDPOINTS} from "../../utils/constants";
import {FaEnvelope, FaGithubAlt, FaLinkedinIn, FaMapMarker, FaPhone} from 'react-icons/fa';

const icons = {FaEnvelope, FaPhone, FaMapMarker, FaLinkedinIn, FaGithubAlt};

function Item(data) {
  const Icon = icons[data.type];

  return (
    <a className="Contacts--item" href={data.url}>
      <Icon className="Contacts--item-icon"/>
      <span className="Contacts--item-text">{data.value}</span>
    </a>
  );
}

function Contacts(props) {

  const {loading, data} = useFetch(API_ENDPOINTS.contacts);

  return (
    <section className="Contacts">
      <h3>Contacts</h3>
      {loading ?
        <div>Loading...</div>
        :
        data.map(Item)

      }
    </section>
  );
}

const AddContactForm = props => {
  const initialFormState = { value: '', type: '', url: '' };
  const [contact, setContact] = useState(initialFormState)

  const handleInputChange = event => {
    const { name, value } = event.target;

    setContact({ ...contact, [name]: value })
  };

  return (
    <form
      onSubmit={event => {
        event.preventDefault()
        if (!user.name || !user.username) return

        props.addUser(user)
        setUser(initialFormState)
      }}
    >
      <label>Name</label>
      <input type="text" name="name" value={user.name} onChange={handleInputChange} />
      <label>Username</label>
      <input type="text" name="username" value={user.username} onChange={handleInputChange} />
      <button>Add new user</button>
    </form>
  )
}

const AddUserForm = props => {
  const initialFormState = { id: null, name: '', username: '' }
  const [user, setUser] = useState(initialFormState)

  const handleInputChange = event => {
    const { name, value } = event.target

    setUser({ ...user, [name]: value })
  }

  return (
    <form
      onSubmit={event => {
        event.preventDefault()
        if (!user.name || !user.username) return

        props.addUser(user)
        setUser(initialFormState)
      }}
    >
      <label>Name</label>
      <input type="text" name="name" value={user.name} onChange={handleInputChange} />
      <label>Username</label>
      <input type="text" name="username" value={user.username} onChange={handleInputChange} />
      <button>Add new user</button>
    </form>
  )
}


export default Contacts;
